import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { Route, withRouter } from 'react-router-dom';
import { setRedirectUrl } from '../../actions/loginActions.js';
import Home from '../../home.js';
class EnsureLoggedInContainer extends React.Component {

  componentDidMount() {
    const { dispatch, currentURL, loggedIn } = this.props;

    if (!loggedIn) {
      dispatch(setRedirectUrl(this.props.history.location.pathname));
      this.props.history.push("/login");
    }
  }

  render() {
    if (this.props.loggedIn) {
      console.log("Logged in");
      return this.props.children
    } else {
      return null
    }
  }
}

EnsureLoggedInContainer.propTypes = {
  dispatch: PropTypes.func.isRequired,
  loggedIn: PropTypes.bool.isRequired,
};

function mapStateToProps(state, ownProps) {
  return {
    loggedIn: state.login.loggedIn,
  }
}

export default withRouter(connect(mapStateToProps)(EnsureLoggedInContainer));